import axios from "axios";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom"

export const MostrarUsuarios = () => {
    const [dataUsuarios, setDataUsuarios] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:4000/api/mostrarUsuarios')
            .then((respuesta) => {
                console.log(respuesta.data)
                setDataUsuarios(respuesta.data)
            })
            .catch((err) => {
                console.log(err)
            })
    }, []);

    const listaUsuarios = dataUsuarios.map((usuario, id) => {
        var linkEditar = "/editarUsuario/" + usuario._id
        var linkBorrar = "/borrarUsuario/" + usuario._id
        return (<tr key={id}>
            <td>{usuario.usuario}</td>
            <td>{usuario.password}</td>
            <td><Link to={linkEditar}>Editar</Link>/<Link to={linkBorrar}>Borrar</Link></td>
        </tr>)
    });

    console.log(listaUsuarios)
    return (
        <table className="table table-hover">
            <thead>
                <tr>
                    <th>Usuario</th>
                    <th>Password</th>
                    <th>Editar / Borrar</th>
                </tr>
            </thead>
            <tbody>
                {listaUsuarios}
            </tbody>
        </table>
    );
}
