import { useParams } from "react-router-dom"
import { useState,useEffect } from "react"
import axios from "axios"
import swal from 'sweetalert'

export const EditarUsuario = () => {
    const [nombre,setNombre]=useState('');
    const [usuario,setUsuario]=useState('');
    const [password,setPassword]=useState('');
    const params = useParams()
    const id = params.id

    useEffect(() => {
        axios.get('http://localhost:4000/api/buscar/'+params.id)
            .then((respuesta) => {
                setNombre(respuesta.data.nombre)
                setPassword(respuesta.data.password)
                setUsuario(respuesta.data.usuario)
            })
            .catch((err) => {
                console.log(err)
            })
    }, []);

    async function editarUsuario(e) {
        e.preventDefault()
        try {
            const usuarioObj={
                id,
                nombre,
                usuario,
                password
            }
            await axios.post('http://localhost:4000/api/modificarUsuario',usuarioObj)
            swal("Listo", "El registro a sido actualizado", "success");
        } catch (error) {
            console.log(error)
        }

    }

    return (<>
        <h1>Editar Usuario</h1>
        <form onSubmit={editarUsuario}>
        <div className="row">
            <div className="col-6 mb-3">
                <input type="text" placeholder="Nombre" className="form-control" value={nombre} onChange={(e)=>{setNombre(e.target.value)}}/>
            </div>
        </div>
        <div className="row">
            <div className="col-6 mb-3">
                <input type="text" placeholder="Usuario" className="form-control" value={usuario} onChange={(e)=>{setUsuario(e.target.value)}}/>
            </div>
        </div>
        <div className="row">
            <div className="col-6 mb-3">
                <input type="password" placeholder="Password" className="form-control" value={password} onChange={(e)=>{setPassword(e.target.value)}}/>
            </div>
        </div>
        <div className="row">
            <div className="col-6">
                <input type="submit" value='Enviar' className="btn btn-primary"/>
            </div>
        </div>
    </form>
    </>)
}
