import {
    BarChart,
    Bar,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend, ResponsiveContainer
} from "recharts";
import React, { useState, useContext, useCallback, useEffect } from 'react';
import { SocketContext } from '../socket';

var data = [
    {
        nombre: 'Page A',
        cantidad: 4000,
        frescos: 2400,
        podridos: 2400,
        fechaRegistro:''
    }
];
console.log(data)

export const Records = () => {
    const socket = useContext(SocketContext);

    const [joined, setJoined] = useState(false);

    const handleInviteAccepted = useCallback(() => {
        setJoined(true);
    }, []);

    useEffect(() => {
        socket.emit('cliente:buscarFrutaDia','25/03/2023')
        socket.on('servidor:verFrutaDiaria', (frutos) => {
            data = frutos
            console.log(data)
        })
    }, [socket, handleInviteAccepted]);
    return (<>
        <div className="header-ini">
            <h1>Records</h1>
        </div>
        <div className="bg-light text-center py-3 d-flex flex-column">
            <h2>Ver la cantidad de frutas frescas o maduras en un dia</h2>
            <h3>Selecciona el dia</h3>
            <br></br>
            <form action="">
                <input type="date" name="" id="" />
                <button type="submit">Buscar Datos</button>
            </form>
            <ResponsiveContainer width="100%" aspect={3}>
                <BarChart
                    width={500}
                    height={300}
                    data={data}
                    margin={{
                        top: 20,
                        right: 30,
                        left: 20,
                        bottom: 5,
                    }}
                >
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey='nombre' />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey="frescos" stackId="a" fill="#8884d8" />
                    <Bar dataKey="podridos" stackId="a" fill="#82ca9d" />
                </BarChart>
            </ResponsiveContainer>
        </div>
    </>)
}
