import ReactDOM from "react-dom/client";
import { NuevoUsuario } from "./components/nuevoUsuario";
import { MostrarUsuarios } from "./components/mostrarUsuarios";
import { Inicio } from "./components/inicio";
import { Error } from "./components/error";
import { EditarUsuario } from "./components/editarUsuario";
import { BorrarUsuario } from "./components/borrarUsuario";
import { Menu } from "./components/menu";
import { BrowserRouter, Routes, Route } from "react-router-dom"
import { Footer } from "./components/footer";
import { About } from "./components/nosotros";
import { Login } from "./components/login";

import React, { useState, useEffect } from 'react';
import { socket } from './socket.js';
/*import { ConnectionState } from './components/ConnectionState';
import { ConnectionManager } from './components/ConnectionManager';
import { MyForm } from './components/MyForm';*/

export default function App() {
    const [isConnected, setIsConnected] = useState(socket.connected);
    const [fooEvents, setFooEvents] = useState([]);

    useEffect(() => {
        function onConnect() {
            setIsConnected(true);
        }

        function onDisconnect() {
            setIsConnected(false);
        }

        function onFooEvent(value) {
            setFooEvents(previous => [...previous, value]);
        }

        socket.on('connect', onConnect);
        socket.on('disconnect', onDisconnect);
        socket.on('foo', onFooEvent);

        return () => {
            socket.off('connect', onConnect);
            socket.off('disconnect', onDisconnect);
            socket.off('foo', onFooEvent);
        };
    }, []);
    return (
        <BrowserRouter>
            <Menu />
            <Routes>
                <Route path="*" element={<Error />}></Route>
                <Route index element={<Inicio />}></Route>
                <Route path="/nuevoUsuario" element={<NuevoUsuario />}></Route>
                <Route path="/mostrarUsuarios" element={<MostrarUsuarios />}></Route>
                <Route path="/editarUsuario/:id" element={<EditarUsuario />}></Route>
                <Route path="/borrarUsuario/:id" element={<BorrarUsuario />}></Route>
                <Route path="/about" element={<About />}></Route>
                <Route path="/login" element={<Login />}></Route>
            </Routes>
            <Footer />
        </BrowserRouter>)
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<App/>);
